<script>
import { store } from '../store.js';
export default {
  data() {
      return {
          store,
      }
  },
  props: {
    movies: Array,
    series: Array,
  },
  methods: {
    getFlagUrl(language) {
      if (language === 'en') {
        return 'https://flagsapi.com/GB/shiny/64.png'; 
      } else if (language === 'fr') {
        return 'https://flagsapi.com/FR/shiny/64.png'; 
      } else if (language === 'es') {
        return 'https://flagsapi.com/ES/shiny/64.png'; 
      }
      else if (language === 'ja') {
        return 'https://flagsapi.com/JP/shiny/64.png'; 
      }
      else {
        return 'data:image/png;base64,'; 
      }
    },
  },
}
</script>
<template>
  <div class="container-fluid">
    <div class="row">
      <div class="col-3 my-1 border bg-black " v-for="movie in store.moviesList">
        <div class="d-flex flex-column align-items-center justify-content-center text-center">
          <h6 class="mt-2 text-uppercase">movies</h6>
          <img class="path" :src="movie.poster_path ? ('http://image.tmdb.org/t/p/w780/' + movie.poster_path) : 'data:image/png;base64,'" alt="">
          <h6 class="mt-3">{{ movie.title }}</h6>
          <h6>{{ movie.original_title}}</h6>
          <img class="flag" :src="getFlagUrl(movie.original_language)" alt="Flag"/>
          <h6>{{ movie.vote_average}}</h6>
        </div>
      </div>
    </div>
  </div>
  <div class="container-fluid">
    <div class="row">
      <div class="col-3 my-1 border bg-black" v-for="serie in store.seriesList">
        <div class="d-flex flex-column align-items-center justify-content-center text-center">
          <h6 class="mt-2 text-uppercase">series</h6>
          <img class="path" :src="serie.poster_path ? ('http://image.tmdb.org/t/p/w780/' + serie.poster_path) : 'data:image/png;base64,'" alt="">
          <h6 class="mt-3">{{ serie.name }}</h6>
          <h6>{{ serie.original_name}}</h6>
          <img class="flag" :src="getFlagUrl(serie.original_language)" alt="Flag"/>
          <h6>{{ serie.vote_average}}</h6>
        </div>
      </div>
    </div>
  </div>
</template>
<style lang="scss" scoped>
.flag {
  width: 10%;
}
.path{
  width: 100%;
  height: 300px;
  margin-top: 5px;
}
h6{
  color: white;
}

</style>